window.siteSearch.getIndexConfig=function(){return Object.assign({encode:!1,tokenize:function(e){return e.replace(/[\x00-\x7F]/g,"").split("")}},{includeScore:!0,useExtendedSearch:!0,fieldNormWeight:1.5,threshold:.2,ignoreLocation:!0,keys:[{name:"title",weight:.7},{name:"content",weight:.3}]})},window.siteSearch.initIndex=async function(){if(window.siteSearch.index)return Promise.resolve(window.siteSearch.index);const e=window.siteSearch.getIndexConfig();return fetch(window.siteSearch.contentUrl).then(e=>{if(!e.ok)throw new Error(`HTTP error! status: ${e.status}`);return e.json()}).then(t=>(window.siteSearch.index=new Fuse(t,e),window.siteSearch.total=t.length,window.siteSearch.index))},window.siteSearch.initCategories=async function(){return window.siteSearch.categories?Promise.resolve(window.siteSearch.categories):fetch(window.siteSearch.categoriesUrl).then(e=>{if(!e.ok)throw new Error(`HTTP error! status: ${e.status}`);return e.json()}).then(e=>(window.siteSearch.categories=e,window.siteSearch.categories))},window.siteSearch.initTags=async function(){return window.siteSearch.tags?Promise.resolve(window.siteSearch.tags):fetch(window.siteSearch.tagsUrl).then(e=>{if(!e.ok)throw new Error(`HTTP error! status: ${e.status}`);return e.json()}).then(e=>(window.siteSearch.tags=e,window.siteSearch.tags))}